import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  TextField,
  Button,
  Box,
  Container,
  Typography,
  FormControlLabel,
  Checkbox,
} from '@mui/material';

const validationSchema = Yup.object().shape({
  username: Yup.string().required('Username is required'),
  password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
});

const LoginScreen = () => {
  const handleLogin = (values, { setFieldError }) => {
    // Perform validation only when "Ingresar" button is clicked
    validationSchema.validate(values, { abortEarly: false })
      .then(() => {
        // Your login logic here
        console.log(values);
      })
      .catch((err) => {
        // Set form field errors based on validation results
        err.inner.forEach((error) => {
          setFieldError(error.path, error.message);
        });
      });
  };

  return (
    <Box
      sx={{
        backgroundColor: '#f5f5f5',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Container
        maxWidth="sm"
        sx={{
          backgroundColor: 'white',
          padding: '24px',
          borderRadius: '8px',
          boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Login MockUp
        </Typography>
        <Formik
          initialValues={{ username: '', password: '', rememberMe: false }}
          onSubmit={handleLogin}
        >
          {({ errors, touched, setFieldError }) => (
            <Form>
              <Field
                name="username"
                as={TextField}
                label="Usuario"
                variant="outlined"
                fullWidth
                sx={{ marginBottom: '16px' }}
              />
              {touched.username && errors.username && (
                <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
                  {errors.username}
                </Typography>
              )}

              <Field
                name="password"
                as={TextField}
                label="ContraseÃ±a"
                variant="outlined"
                type="password"
                fullWidth
                sx={{ marginBottom: '16px' }}
              />
              {touched.password && errors.password && (
                <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
                  {errors.password}
                </Typography>
              )}

              <Field
                name="rememberMe"
                type="checkbox"
              >
                {({ field }) => (
                  <FormControlLabel
                    control={<Checkbox {...field} />}
                    label="Recordar nombre de usuario"
                    sx={{ marginBottom: '16px', alignItems: 'center', justifyContent: 'center', display: 'flex' }}
                  />
                )}
              </Field>

              <Button type="submit" variant="contained" sx={{ marginBottom: '8px', width: '100%' }}>
                Ingresar
              </Button>
              <Button variant="outlined" sx={{ width: '100%' }}>
                Quiero crear una cuenta
              </Button>
            </Form>
          )}
        </Formik>
      </Container>
    </Box>
  );
};

export default LoginScreen;
