import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  TextField,
  Button,
  Typography,
  FormControlLabel,
  Checkbox,
} from '@mui/material';

const validationSchema = Yup.object().shape({
  username: Yup.string().required('Username is required'),
  password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
});

const LoginForm = ({ onSubmit }) => {
  return (
    <Formik
      initialValues={{ username: '', password: '', rememberMe: false }}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
      validateOnBlur={false}
      validateOnChange={false}
    >
      {({ errors, isValidating, validateForm }) => (
        <Form>
          <Field
            name="username"
            as={TextField}
            label="Username"
            variant="outlined"
            fullWidth
            sx={{ marginBottom: '16px', backgroundColor: 'rgb(92, 131, 116)' }}
          />
          {errors.username && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', margin: '8px' }}>
              {errors.username}
            </Typography>
          )}

          <Field
            name="password"
            as={TextField}
            label="ContraseÃ±a"
            variant="outlined"
            type="password"
            fullWidth
            sx={{ marginBottom: '16px', backgroundColor: 'rgb(92, 131, 116)' }}
          />
          {errors.password && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
              {errors.password}
            </Typography>
          )}

          <Field
            name="rememberMe"
            type="checkbox"
          >
            {({ field }) => (
              <FormControlLabel
                control={<Checkbox {...field} />}
                label="Recordar nombre de usuario"
                sx={{ marginBottom: '16px', alignItems: 'center', justifyContent: 'center', display: 'flex' }}
              />
            )}
          </Field>

          <Button
            type="submit"
            variant="contained"
            sx={{ marginBottom: '8px', width: '70%', backgroundColor: 'rgb(147, 177, 166)' }}
            onClick={() => {
              validateForm().then((errors) => {
                const hasErrors = Object.values(errors).some(Boolean);
                if (!hasErrors) {
                  onSubmit();
                }
              });
            }}
            disabled={isValidating}
          >
            Ingresar
          </Button>

        </Form>
      )}
    </Formik>
  );
};

export default LoginForm;
