import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup'; // Import Yup for validation
import {
  TextField,
  Button,
  Box,
  Container,
  Typography,
  FormControlLabel,
  Checkbox,
} from '@mui/material';

const validationSchema = Yup.object().shape({
  username: Yup.string().required('Username is required'),
  password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
});

const LoginScreen = () => {
  const handleLogin = (values) => {
    // Your login logic here
    console.log(values);
  };

  return (
    <Box
      sx={{
        backgroundColor: '#f5f5f5',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Container
        maxWidth="sm"
        sx={{
          backgroundColor: 'white',
          padding: '24px',
          borderRadius: '8px',
          boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
          textAlign: 'center',
        }}
      >
        <Typography variant="h4" gutterBottom>
          Login MockUp
        </Typography>
        <Formik
          initialValues={{ username: '', password: '', rememberMe: false }}
          onSubmit={handleLogin}
          validationSchema={validationSchema} // Apply validation schema
        >
          {({ errors, touched }) => (
            <Form>
              <Field
                name="username"
                as={TextField}
                label="Usuario"
                variant="outlined"
                fullWidth
                sx={{ marginBottom: '16px' }}
              />
              <ErrorMessage name="username" component="div" className="error" />

              <Field
                name="password"
                as={TextField}
                label="ContraseÃ±a"
                variant="outlined"
                type="password"
                fullWidth
                sx={{ marginBottom: '16px' }}
              />
              <ErrorMessage name="password" component="div" className="error" />

              <Field
                name="rememberMe"
                type="checkbox"
                as={Checkbox}
                label="Recordar nombre de usuario"
                sx={{ marginBottom: '16px', alignItems: 'center', justifyContent: 'center', display: 'flex' }}
              />

              <Button type="submit" variant="contained" sx={{ marginBottom: '8px', width: '100%' }}>
                Ingresar
              </Button>
              <Button variant="outlined" sx={{ width: '100%' }}>
                Quiero crear una cuenta
              </Button>
            </Form>
          )}
        </Formik>
      </Container>
    </Box>
  );
};

export default LoginScreen;
