import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  TextField,
  Button,
  Typography,
  FormControlLabel,
  Checkbox,
  Box,
} from '@mui/material';

const validationSchema = Yup.object().shape({
  username: Yup.string().required('Username is required'),
  password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
});

const LoginForm = ({ onSubmit }) => {
  return (
    <Formik
      initialValues={{ username: '', password: '', rememberMe: false }}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
    >
      {({ errors, touched }) => (
        <Form>
          <Field
            name="username"
            as={TextField}
            label="Usuario"
            variant="outlined"
            fullWidth
            sx={{ marginBottom: '16px' }}
          />
          {touched.username && errors.username && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
              {errors.username}
            </Typography>
          )}

          <Field
            name="password"
            as={TextField}
            label="ContraseÃ±a"
            variant="outlined"
            type="password"
            fullWidth
            sx={{ marginBottom: '16px' }}
          />
          {touched.password && errors.password && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
              {errors.password}
            </Typography>
          )}

          <Field
            name="rememberMe"
            type="checkbox"
          >
            {({ field }) => (
              <FormControlLabel
                control={<Checkbox {...field} />}
                label="Recordar nombre de usuario"
                sx={{ marginBottom: '16px', alignItems: 'center', justifyContent: 'center', display: 'flex' }}
              />
            )}
          </Field>

          {/* Buttons */}
          <Box sx={{ textAlign: 'center', '@media (max-width: 600px)': { '& button': { width: '100%', marginBottom: '8px' } } }}>
            <Button type="submit" variant="contained">
              Ingresar
            </Button>
            <Button variant="outlined">
              Quiero crear una cuenta
            </Button>
          </Box>
        </Form>
      )}
    </Formik>
  );
};

export default LoginForm;
