import React from 'react';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import {
  TextField,
  Button,
  Typography,
  FormControlLabel,
  Checkbox,
} from '@mui/material';

const validationSchema = Yup.object().shape({
  username: Yup.string().required('Username is required'),
  password: Yup.string().min(8, 'Password must be at least 8 characters').required('Password is required'),
});

const LoginForm = ({ onSubmit }) => {
  return (
    <Formik
      initialValues={{ username: '', password: '', rememberMe: false }}
      onSubmit={onSubmit}
    >
      {({ errors, touched, setFieldError, isValidating }) => (
        <Form>
          <Field
            name="username"
            as={TextField}
            label="Usuario"
            variant="outlined"
            fullWidth
            sx={{ marginBottom: '16px' }}
          />
          {touched.username && errors.username && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
              {errors.username}
            </Typography>
          )}

          <Field
            name="password"
            as={TextField}
            label="ContraseÃ±a"
            variant="outlined"
            type="password"
            fullWidth
            sx={{ marginBottom: '16px' }}
          />
          {touched.password && errors.password && (
            <Typography variant="caption" color="error" sx={{ textAlign: 'left', marginBottom: '8px' }}>
              {errors.password}
            </Typography>
          )}

          <Field
            name="rememberMe"
            type="checkbox"
          >
            {({ field }) => (
              <FormControlLabel
                control={<Checkbox {...field} />}
                label="Recordar nombre de usuario"
                sx={{ marginBottom: '16px', alignItems: 'center', justifyContent: 'center', display: 'flex' }}
              />
            )}
          </Field>

          <Button
            type="submit"
            variant="contained"
            sx={{ marginBottom: '8px', width: '100%' }}
            onClick={() => {
              validationSchema.validate({ username: values.username, password: values.password }, { abortEarly: false })
                .then(() => {
                  onSubmit(values);
                })
                .catch((err) => {
                  err.inner.forEach((error) => {
                    setFieldError(error.path, error.message);
                  });
                });
            }}
            disabled={isValidating}
          >
            Ingresar
          </Button>
          <Button variant="outlined" sx={{ width: '100%' }}>
            Quiero crear una cuenta
          </Button>
        </Form>
      )}
    </Formik>
  );
};

export default LoginForm;
